import React, { useEffect } from "react";
import { Link, useN } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux'
import logo from "../../assets/logo.png";
import search from '../../assets/search-solid.svg'
import Avatar from '../../components/Avatar/Avatar'
import Button from '../../components/Button/Button'
import "./Navbar.css";
import { setCurrentUser } from "../../actions/currentUser";

const Navbar = () => {
  const dispatch = useDispatch()
  var User = useSelector((state) => (state.currentUserReducer));
  const navigate = useNavigate();

  const handleLogout = () => {
    dispatch({ type: "LOGOUT" });
    navigate("/");
    dispatch(setCurrentUser(null));
  };

  useEffect(() =>  {
    dispatch(setCurrentUser( JSON.parse(localStorage.getItem('Profile'))))
  },[dispatch])

    return (
        <nav>
            <div className='navbar'>
                <Link to='/' className='nav-item nav-btn'>
                    <img src={logo} alt='logo' />
                </Link>
                <Link to='/' className='nav-item nav-btn'>About</Link>
                <Link to='/' className='nav-item nav-btn'>Products</Link>
                <Link to='/' className='nav-item nav-btn'>For Teams</Link>
                <form>
                  <input type="text" placeholder='Search...' />
                  <img src={search} alt="search" width="18" />
                </form>
                { User === null ?
                    <Link to='/Auth' className='nav-item nav-links'>Log In</Link> :
                    <>
                        <Link to='/User' style={{color: "white", textDecoration: 'none'}}>
                        <Avatar backgroundColor='#009dff' px="10px" py="7px" borderRadius="50%" color='white'>S</Avatar></Link>
                        <Button>
                        className="nav-item nav-links" onClick={handleLogout}Log out
                        </Button>
                        </>
              }
            </div>  
        </nav>
      )
}

export default Navbar
